import React, { useState, useEffect } from 'react';
import ROSLIB from 'roslib';
import './App.css';
import publishIcon from './publish-icon.png';
import yoloIcon from './yolo-icon.png';

function App() {
  const [connected, setConnected] = useState(false);
  const [ros, setRos] = useState(null);

  useEffect(() => {
    const newRos = new ROSLIB.Ros({
      url: 'ws://localhost:9090'
    });

    newRos.on('connection', () => {
      console.log('Connected to websocket server.');
      setConnected(true);
    });

    newRos.on('error', (error) => {
      console.log('Error connecting to websocket server: ', error);
      setConnected(false);
    });

    newRos.on('close', () => {
      console.log('Connection to websocket server closed.');
      setConnected(false);
    });

    setRos(newRos);

    return () => {
      if (newRos) {
        newRos.close();
      }
    };
  }, []);

  const publishMessage = () => {
    if (ros) {
      const topic = new ROSLIB.Topic({
        ros: ros,
        name: '/test_topic',
        messageType: 'std_msgs/String'
      });

      const message = new ROSLIB.Message({
        data: 'Hello from React!'
      });

      topic.publish(message);
    }
  };

  const runROS2Node = async () => {
    try {
      const response = await fetch('http://localhost:3001/run-ros2-node', {
        method: 'POST',
      });
      const data = await response.json();
      console.log(data.message);
    } catch (error) {
      console.error('Error running ROS2 node:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ROS 2 Humble React App</h1>
        <p className="sub-title">Connection status: {connected ? 'Connected' : 'Disconnected'}</p>
      </header>
      <div className="button-grid">
        <div className="button-wrapper">
          <img src={publishIcon} alt="Publish" className="button-icon" />
          <button className="robot-button" onClick={publishMessage}>Publish Message</button>
        </div>
        <div className="button-wrapper">
          <img src={yoloIcon} alt="YOLOv8" className="button-icon" />
          <button className="robot-button" onClick={runROS2Node}>Run YOLOv8 Node</button>
        </div>
        {/* Add more button-wrapper divs here for additional buttons */}
      </div>
    </div>
  );
}

export default App;
